void outputBint(const AEBigint& bint) {
	cout << "--------------------------------------------" << NLC;
	cout << "The size of the bigint (digits): " << bint.size()<<NLC;
	cout << "The amount of number sectors: " << bint.getSectorAmount() << NLC;
	cout << "The memory usage of the bigint (bytes): " << bint.getMemoryUsage() << " (sizeof(AEBigint) = " <<sizeof(AEBigint)<<")" << NLC;
	cout << "The bigint is negative: " << ace::utils::boolToString(bint.isNegative()) << NLC;
	cout << "The value of the bigint: " << bint << NLC;
	cout << "--------------------------------------------" << NLC;

}

constexpr long double runningAvg(const int amt, const long double val, const long double newval) noexcept{
	return (val * amt + newval) / (amt + 1);
}

void timechecker(const ullint exp, std::atomic<ullint>& progress) {
	AEFrame myfr(1000);
	AETimer mytm(1000); // 1 tick per second

	ullint curprog = 0;
	while (curprog < exp) {
		curprog = progress.load(std::memory_order::relaxed);
		cout << "Current progress: 2^" << curprog << " out of 2^" << exp << " (" << (double(curprog) / exp * 100) << "% done); Elapsed time: " << mytm.getWorldTime() << " seconds " << NLC;
		myfr.sleep();

	}
	mytm.stopThread();
	cout << "Finished calculating 2^" << exp << " Took time: " << mytm.getWorldTime() << " seconds" << NLC << "Exiting progress counter...";
}

AEBigintSector getnum() {
	AEBigintSector num = 0;
	for (int i = 0; i < _AEBI_MAX_SECTOR_STORE_DIGITS; i++) {
		num *= 10;
		num += 9;
	}
	return num;
}


